// Advent Day 2, First Puzzle
#include <iostream>
#include <string>
using namespace std;

string decToHex(int num)
{
    switch (num)
    {
        case 10 : return "A";
        case 11 : return "B";
        case 12 : return "C";
        case 13 : return "D";
        default : return to_string(num);
    }
}
int main()
{
    // initialize variables and input
    string input = "    ";
    string result;
    int position = 5;
    
    
    // repeat the following for each character in input
    // - if current position is a blank space, store current position (append to string)
    // - attempt to move in a direction
    // - if impossible, keep current position
    for(int i = 0; i < input.length(); i++)
    {
        switch(input[i])
        {
            case 'L' :
                position -= (position == 1 || position == 2 || position == 5 || position == 10 || position == 13) ? 0 : 1;
                break;
            case 'R' :
                position += (position == 1 || position == 4 || position == 9 || position == 12 || position == 13) ? 0 : 1;
                break;
            case 'U' :
                position -= (position == 1 || position == 2 || position == 4 || position == 5 || position == 9) ? 0 : (position == 3 || position == 13 ? 2 : 4);
                break;
            case 'D' :
                position += (position == 5 || position == 9 || position == 10 || position == 12 || position == 13) ? 0 : (position == 1 || position == 11 ? 2 : 4); 
                break;
            case ' ' :
                result += decToHex(position);           
        }
    }
    result += decToHex(position);
    cout << result;
}
